import { useState } from "react";
import { styled } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Paper from "@mui/material/Paper";
import Grid from "@mui/material/Grid";
import { Container } from "@mui/material";

//component
import SymbolsFetch from "./Components/SymbolsData";
import SetCurrencyData from "./Components/SetCurrencyData";
import ConvertRates from "./Components/ConvertRates";

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: "#2a2826",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: "#fff",
  ...theme.applyStyles("kight", {
    backgroundColor: "#1A2027",
  }),
}));

export default function BasicGrid() {
  const [symbols, setSymbols] = useState({});
  const [fromCurrency, setFromCurrency] = useState("USD");
  const [toCurrency, setToCurrency] = useState("PHP");
  const [amount, setAmount] = useState("");
  const [converted, setConverted] = useState(null);

  return (
    <Container>
      <Box sx={{ flexGrow: 1, color: "white" }}>
        <Grid container spacing={2} rowSpacing={1} columnSpacing={{ xs: 1, sm: 2, md: 3, lg:1 }}>
          <Grid item  size={12}>
            <Item sx={{ p: 2 }}>
              {converted ? (
                <p>
                  {amount} {fromCurrency} = {converted.toFixed(2)} {toCurrency}
                </p>
              ) : (
                <p>Please enter an amount and select currencies to convert.</p>
              )}
            </Item>
          </Grid>
          <Grid item xs={12} size={4}>
            <SymbolsFetch
              Item={Item}
              symbols={symbols}
              setSymbols={setSymbols}
              fromCurrency={fromCurrency}
              setFromCurrency={setFromCurrency}
            ></SymbolsFetch>
          </Grid>
          <Grid item xs={12} size={4}>
            <ConvertRates
              toCurrency={toCurrency}
              setToCurrency={setToCurrency}
              Item={Item}
              symbols={symbols}
              setSymbols={setSymbols}
              fromCurrency={fromCurrency}
              setFromCurrency={setFromCurrency}
              amount={amount}
              setAmount={setAmount}
              setConverted={setConverted}
            ></ConvertRates>
          </Grid>
          <Grid item xs={12} size={4}>
            <SetCurrencyData item xs={12} sm={6} md={4}
              toCurrency={toCurrency}
              setToCurrency={setToCurrency}
              Item={Item}
              symbols={symbols}
              setSymbols={setSymbols}
              fromCurrency={fromCurrency}
              setFromCurrency={setFromCurrency}
            ></SetCurrencyData>
          </Grid>
          <Grid item sx={12} >
            <Item sx={{ p: 2 }}></Item>
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
}
