import React from "react";
import TextField from "@mui/material/TextField";
import MenuItem from "@mui/material/MenuItem";
import { CircularProgress } from "@mui/material";

function SetCurrencyData({ symbols, toCurrency, setToCurrency, Item,convertCurrency,fromCurrency }) {
  return (
    <Item sx={{ p: 2 }}>
       <div>
        <img
          src={`https://currencyfreaks.com/photos/flags/${convertCurrency(fromCurrency)}.webp`}
          alt={`${convertCurrency(fromCurrency)} flag`}
          style={{ width: 100, height: 71, marginRight: 8 }}
        />
      </div>
      {symbols && Object.keys(symbols).length > 0 ? (

        <TextField
          id="outlined-select-currency"
          select
          label="Select"
          value={toCurrency}
          onChange={(e) => setToCurrency(e.target.value)}
          helperText="Convert To This Currency"
          sx={{
            label: { color: "rgba(255, 255, 255, 0.7)" },
            ".MuiOutlinedInput-root": {
              color: "white", // sets text color
              backgroundColor: "#1e1e1e", // optional: input background
              "& fieldset": {
                borderColor: "rgba(255, 255, 255, 0.5)",
              },
              "&:hover fieldset": {
                borderColor: "white",
              },
              "&.Mui-focused fieldset": {
                borderColor: "#90caf9",
              },
            },
            ".MuiSelect-select": {
              color: "white", // selected text color
              backgroundColor: "#1e1e1e", // optional: selected input background
            },
            ".MuiSelect-icon": {
              color: "white",
            },
            "& .MuiFormHelperText-root": {
              color: "rgba(255,255,255,0.6)",
            },
          }}
        >
          {symbols &&
            Object.entries(symbols).map(([key, item]) => (
              <MenuItem key={key} value={key}>
                {key} - {item}
              </MenuItem>
            ))}
        </TextField>
      ) : (
        <CircularProgress sx={{ color: '#7affae' }}></CircularProgress>
      )}
    </Item>
  );
}

export default SetCurrencyData;
