import React, { useEffect } from "react";
import axios from "axios";
import InputLabel from "@mui/material/InputLabel";
import FormControl from "@mui/material/FormControl";
import OutlinedInput from "@mui/material/OutlinedInput";
import { FormHelperText } from "@mui/material";


function ConvertRates({
  Item,
  symbols,
  fromCurrency,
  toCurrency,
  amount,
  setAmount,
  setConverted,
  date
}) {
  useEffect(() => {
    if (!amount || !fromCurrency || !toCurrency || !symbols ) return;

    const fetchRates = async () => {
      try {
        const res = await axios.get("https://data.fixer.io/api/latest", {
          params: {
            access_key: "dc8091b34903d6483857ed61900c5f3a",
            symbols: `${fromCurrency},${toCurrency}`,
          },
        });

        const rates = res.data.rates;
        const fromRate = rates[fromCurrency];
        const toRate = rates[toCurrency];
        const date = res.data.date;
        const result = (toRate / fromRate) * amount;
        setConverted(result);
      } catch (error) {
        console.error("Conversion error:", error.message);
      }
    };

    fetchRates();
  }, [amount, fromCurrency, toCurrency, symbols, setConverted, date]);

  return (
    <Item sx={{ p: 2,  }}>
      <FormControl variant="outlined">
        <InputLabel
          htmlFor="outlined-adornment-amount"
          sx={{ color: "rgba(255, 255, 255, 0.7)" }}
        >
          Amount
        </InputLabel>
        <OutlinedInput
          id="outlined-adornment-amount"
          type="number"
          value={amount}
          onChange={(e) => setAmount(Number(e.target.value))}
          label="Amount"
          sx={{
            // Remove background
            backgroundColor: "transparent",

            // Text color
            color: "white",

            // Color of the input text specifically
            input: {
              color: "white",
            },

            // Border styling
            ".MuiOutlinedInput-notchedOutline": {
              borderColor: "rgba(255, 255, 255, 0.5)",
            },
            "&:hover .MuiOutlinedInput-notchedOutline": {
              borderColor: "white",
            },
            "&.Mui-focused .MuiOutlinedInput-notchedOutline": {
              borderColor: "#90caf9",
            },

            // Optional: caret color
            caretColor: "white",
          }}
        />
         <FormHelperText sx={{ color: "rgba(255, 255, 255, 0.6)" }}>
    Please enter the amount to convert.
  </FormHelperText>
      </FormControl>
    </Item>
  );
}

export default ConvertRates;
